/*
* Util function to get vender identifier details and choose the xslt file according to the vendor
*/
function getManufacturerModelName(xmlData) {
	try {
		if (xmlData) {
			var ccdaSchemaFolder = channelMap.get('ccdaSchemaFolder');
			var xmlDoc = new XML(xmlData); // Convert XML string to XML object
			var cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter.xslt";
			var cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle.xslt";

			var manufacturerModelName = '';
			var ehrVendor = '';
			var lowerName = '';
			var modelNode = '';

			manufacturerModelName = xmlDoc.*::author.*::assignedAuthor.*::assignedAuthoringDevice.*::manufacturerModelName.toString();
			if (manufacturerModelName != undefined && manufacturerModelName.toString().length > 0) {
				logger.info("Extracted Manufacturer Model Name: " + manufacturerModelName);
				lowerName = manufacturerModelName.toLowerCase();
			} else {
			     logger.warn("manufacturerModelName is empty");
			}

			if (lowerName.indexOf("epic") !== -1) {
			    	ehrVendor = "Epic";
			    	cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter-epic.xslt";
				cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle-epic.xslt";
			} else if (lowerName.indexOf("medent") !== -1) {
			    	ehrVendor = "Medent";
			    	cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter-medent.xslt";
				cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle-medent.xslt";
			} else {
			    	ehrVendor = "Other";
			    	cdaPhiFilterXsltPath = ccdaSchemaFolder + "/cda-phi-filter.xslt";
				cdaFhirBundleXsltPath = ccdaSchemaFolder + "/cda-fhir-bundle.xslt";
			}

			logger.info("ehrVendor: " + ehrVendor);
			channelMap.put('ehrVendor', ehrVendor);
			channelMap.put('cdaPhiFilter', cdaPhiFilterXsltPath);
			channelMap.put('cdaPhiBundle', cdaFhirBundleXsltPath);

			logger.info("using cdaPhiFilter: " + channelMap.get('cdaPhiFilter'));
			logger.info("using cdaPhiBundle: " + channelMap.get('cdaPhiBundle'));

		} else {
		    logger.error("xmlData is undefined or empty.");
		}
	} catch (e) {
        //logger.warn("Error parsing manufacturerModelName: " + e);
        var errorMsg;
        if (e instanceof JavaException) {
           errorMsg = "Error parsing manufacturerModelName: " + e.toString();
        } else {
           errorMsg = "Unexpected error during validation: " + e.message;
        }
        logger.error(errorMsg);

        // Failure: Return an OperationOutcome JSON response with validation errors
        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));

        throw new Error(errorMsg);
     }
}

/**
* Util function to generate a hash string using sha-256 that can be used as the resource id in FHIR Bundle.
*/
importPackage(java.security);
importPackage(java.math);
importPackage(javax.xml.parsers);
importPackage(org.w3c.dom);
importPackage(javax.xml.xpath);

function generateSHA256(inputString) {
    var md = MessageDigest.getInstance("SHA-256");
    md.update(inputString.getBytes("UTF-8"));   
    var digest = md.digest();
    return bytesToHex(digest);
}

function bytesToHex(byteArray) {
    var hexString = new java.lang.StringBuilder();
    for (var i = 0; i < byteArray.length; i++) {
        var hex = java.lang.Integer.toHexString(0xff & byteArray[i]);
        if (hex.length == 1) hexString.append('0');
        hexString.append(hex);
    }
    return hexString.toString();
}

// Define Namespace Resolver
function getElementsByTagNameNS(node, namespaceURI, localName) {
    return node.getElementsByTagNameNS(namespaceURI, localName);
}

function getPatientMRN(patientResource) {
    var extensionAttr = '';

    function isSSNFormat(ext) {
    	    return (
	        String.fromCharCode(ext.charCodeAt(3)) == '-' &&
	        String.fromCharCode(ext.charCodeAt(6)) == '-' &&
	        /^\d{3}-\d{2}-\d{4}$/.test(ext)
	    );
    }

    if (patientResource.getLength() > 0) {
        var resourceNode = patientResource.item(0);
        var idNodes = resourceNode.getElementsByTagNameNS("urn:hl7-org:v3", "id");

        // Step 1: Check for Medent MRN
        if (channelMap.get('ehrVendor') == 'Medent') {
        	   for (var j = 0; j < idNodes.getLength(); j++) {
                var idNode = idNodes.item(j);
                var root = idNode.getAttribute("root");
                var ext = idNode.getAttribute("extension");

                if (
                    root != "2.16.840.1.113883.4.1" &&  // Not SSN root
                    !isSSNFormat(ext)
                ) {
                    extensionAttr = ext;
                    logger.info("Patient MRN (Medent): " + extensionAttr);
                    break;
                }
             }	        
        }
	   else {	
        // Step 2: If Epic not found, fallback to Epic logic
            for (var i = 0; i < idNodes.getLength(); i++) {
	            var idNode = idNodes.item(i);
	            var assigningAuthorityName = idNode.getAttribute("assigningAuthorityName");
	            var root = idNode.getAttribute("root");
	            var ext = idNode.getAttribute("extension");

	            if (
	                assigningAuthorityName == "EPI" &&
	                (root == "1.2.840.114350.1.13.570.2.7.5.737384.14" || root != "1.2.840.114350.1.1") && 
	                !isSSNFormat(ext)
	            ) {
	                extensionAttr = ext;
	                logger.info("Patient MRN (Epic): " + extensionAttr);
	                break;
	            }
	        }
	   }
        if (!extensionAttr) {
            logger.warn("No matching MRN found.");
        }
    } else {
        logger.error("PatientRole not found in the XML.");
    }

    channelMap.put('Patient-MRN', extensionAttr);
}

function getEncounterEffectiveTime(encounterNodeList) {
    var fallbackTime = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
    var effectiveTimeOut = '';

    if (!encounterNodeList || encounterNodeList.getLength() === 0) {
        return fallbackTime;
    }

    var node = encounterNodeList.item(0); // assuming only one is expected
    if (!node) return fallbackTime;

    var effectiveTimeNodes = node.getElementsByTagNameNS("urn:hl7-org:v3", "effectiveTime");
    if (effectiveTimeNodes.getLength() === 0) return fallbackTime;

    var effectiveTime = effectiveTimeNodes.item(0);

    var lowNodes = effectiveTime.getElementsByTagNameNS("urn:hl7-org:v3", "low");
    if (lowNodes.getLength() > 0 && lowNodes.item(0).getAttribute("value")) {
    	   effectiveTimeOut = lowNodes.item(0).getAttribute("value");
    	   logger.info("effectiveTime : " + effectiveTimeOut);
        return effectiveTimeOut;
    }

    if (effectiveTime.getAttribute("value")) {
        effectiveTimeOut = effectiveTime.getAttribute("value");
        logger.info("effectiveTime : " + effectiveTimeOut);
        return effectiveTimeOut;
    }

    return fallbackTime;
}

function generateSHA256Id(resource, resourceName, resourceIdName, transformer, doc, additionalString) {
    if (resource.getLength() > 0) {
	    var resourceNode = resource.item(0); // Get first occurrence
	    var resourceNameElement = doc.createElementNS("urn:hl7-org:v3", "resourceName");
	    resourceNameElement.setTextContent(resourceName); // Set the name dynamically  
	    resourceNode.appendChild(resourceNameElement); // Append the new element to the resource node

	    var resourceText = resourceNode.getTextContent().trim(); // Extract content  

	    var mrn = channelMap.get('Patient-MRN') || "";
         var cin = channelMap.get('patientCIN') || "";
         var combinedText = mrn + cin + resourceText;

         if (typeof additionalString !== 'undefined' && additionalString != null && additionalString !== '') {
            combinedText += additionalString;
            logger.info("Including additionalString in SHA-256: " + additionalString);
         }

	    var sha256Hash = generateSHA256(new java.lang.String(combinedText)); // Wrap in Java String before hashing

	    transformer.setParameter(resourceIdName, sha256Hash);
	    logger.info(resourceIdName + " : " + sha256Hash);
	} else {
	    logger.error(resourceName + " not found in the XML.");
	}
	return transformer;
}

function generateUUID(resource, resourceIdName, transformer) {
	resource = resource + channelMap.get('Patient-MRN');
	logger.info("resource : " + resource);
     if (resource != '' ) {
    	    var resourceId = java.util.UUID.nameUUIDFromBytes(new java.lang.String(resource).getBytes("UTF-8")).toString();	    
	} else {
	    var resourceId = java.util.UUID.randomUUID().toString();
	}
	transformer.setParameter(resourceIdName, resourceId);
	logger.info(resourceIdName + " : " + resourceId);
	return transformer;
}

function getCategoryDisplay(code) {
    switch (code) {
        case '71802-3':
            return 'Housing Instability';
        case '96778-6':
            return 'Inadequate Housing';
        case '96779-4':
            return 'Utility Insecurity';
        case '88122-7':
        case '88123-5':
            return 'Food Insecurity';
        case '93030-5':
            return 'Transportation Insecurity';
        case '96780-2':
            return 'Employment Status';
        case '96782-8':
            return 'Education/Training';
        case '95618-5':
        case '95617-7':
        case '95616-9':
        case '95615-1':
        case '95614-4':
            return 'Interpersonal Safety';
        default:
            return 'SDOH Category Unspecified';
    }
}

function getCategoryCode(code) {
    switch (code) {
        case '71802-3':
            return 'housing-instability';
        case '96778-6':
            return 'inadequate-housing';
        case '96779-4':
            return 'utility-insecurity';
        case '88122-7':
        case '88123-5':
            return 'food-insecurity';
        case '93030-5':
            return 'transportation-insecurity';
        case '96780-2':
            return 'employment-status';
        case '96782-8':
        case '95618-5':
        case '95617-7':
        case '95616-9':
        case '95615-1':
        case '95614-4':
            return 'sdoh-category-unspecified';
        default:
            return null;
    }
}

// Define Namespace Resolver
function getObservationCategoryCodes(transformer, observations) {    
    var xml = new XML(observations); // Parse the source message as XML    
    var ccda = new Namespace("urn:hl7-org:v3"); // Define the CCDA namespace    
    default xml namespace = ccda; // Set default namespace so ccda tags can be used without prefix

    // Extract all observation code attributes
    if (channelMap.get('ehrVendor') == 'Medent') {
	    var codes = xml..component
	                   .structuredBody
	                   .component
	                   .section.(@ID == 'observations')
	                   .entry
	                   .observation
	                   .entryRelationship
	                   .observation
	                   .code.@code;
     }
     else {
     	var codes = xml..component
	                   .structuredBody
	                   .component
	                   .section.(@ID == 'observations')
	                   .entry
	                   .observation
	                   .entryRelationship
	                   .observation
	                   .entryRelationship
	                   .observation
	                   .code.@code;
     }

	for (var i = 0; i < codes.length(); i++) {
	    logger.info("Code: " + codes[i]);
	}

	// collect unique category codes
	var categorySet = {};
	for (var i = 0; i < codes.length(); i++) {
	    var code = getCategoryCode(codes[i].toString().trim());
	    if (code && !(code in categorySet)) {
	        categorySet[code] = getCategoryDisplay(codes[i].toString().trim());  
	    }
	}
	for (var code in categorySet) {
	    if (categorySet.hasOwnProperty(code)) {
	        logger.info("Category Code: " + code + ", Display: " + categorySet[code]);
	    }
	}

	var categoryJsonArray = [];
	for (var code in categorySet) {
	    categoryJsonArray.push({
	        code: code,
	        display: categorySet[code]
	    });
	}

	//Build Category json from Category codes to pass to the xslt file as transformer parameter
	var categoryXml = '[';
	for (var i = 0; i < categoryJsonArray.length; i++) {
	    categoryXml += '{' +
	        '"system": "http://hl7.org/fhir/us/sdoh-clinicalcare/CodeSystem/SDOHCC-CodeSystemTemporaryCodes",' +
	        '"code": "' + categoryJsonArray[i].code + '",' +
	        '"display": "' + categoryJsonArray[i].display + '"}';

	    if (i !== categoryJsonArray.length - 1) {
	        categoryXml += ',';
	    }
	}	
	categoryXml += ']';              

	// set context variable
	//msg['categoryXml'] = categoryXml;
	transformer.setParameter('categoryXml', categoryXml);
	logger.info("categoryXml : " + categoryXml);
	return transformer;
}

// Define Namespace Resolver
function setResourceIdParameters(transformer) {
    	// Parse XML
	var sourceXml = $('phi_filtered_ccd');

	var factory = DocumentBuilderFactory.newInstance();

	factory.setNamespaceAware(true);
	var builder = factory.newDocumentBuilder();
	var doc = builder.parse(new java.io.ByteArrayInputStream(sourceXml.getBytes("UTF-8")));

	// Generate Bundle resource Id
	var ccda_fhir_bundle_id = channelMap.get('interactionId');
	if (ccda_fhir_bundle_id) {
		transformer.setParameter('bundleId', ccda_fhir_bundle_id);
		logger.info('bundleId : ' + ccda_fhir_bundle_id);
	}
	else {
		var bundle = doc.getElementsByTagNameNS("urn:hl7-org:v3", "ClinicalDocument");
		transformer = generateSHA256Id(bundle, "ClinicalDocument", "bundleId", transformer, doc);		
	}

	// Extract `patientRole`
	var patientRoles = doc.getElementsByTagNameNS("urn:hl7-org:v3", "patientRole");
	getPatientMRN(patientRoles);
	transformer = generateSHA256Id(patientRoles, "patientRole", "patientResourceId", transformer, doc);

	// Extract `consent`
	var consent = doc.getElementsByTagNameNS("urn:hl7-org:v3", "consent");
	var currentTimestamp = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
	transformer = generateSHA256Id(consent, "consent", "consentResourceId", transformer, doc, currentTimestamp);

	// Extract `author (organization)`
	var author = doc.getElementsByTagNameNS("urn:hl7-org:v3", "author");
	transformer = generateSHA256Id(author, "Author", "organizationResourceId", transformer, doc);

	// Extract `Questionnaire`
	var Questionnaire = doc.getElementsByTagNameNS("urn:hl7-org:v3", "section");
	transformer = generateSHA256Id(Questionnaire, "Questionnaire", "questionnaireResourceId", transformer, doc);

	// Extract `observations`
	var observations = doc.getElementsByTagNameNS("urn:hl7-org:v3", "entryRelationship");
	transformer = generateSHA256Id(observations, "observations", "observationResourceSha256Id", transformer, doc);

	// Extract `sexualOrientation`
	var sexualOrientation = doc.getElementsByTagNameNS("urn:hl7-org:v3", "observation");
	transformer = generateSHA256Id(sexualOrientation, "sexualOrientation", "sexualOrientationResourceId", transformer, doc);

	// Extract `procedures`
	var procedures = doc.getElementsByTagNameNS("urn:hl7-org:v3", "procedure");
	if (procedures.getLength() > 0) {
		 transformer = generateSHA256Id(procedures, "procedure", "procedureResourceSha256Id", transformer, doc);
	} else {
	    logger.error("procedure Resource not found in the XML.");
	}

	// Extract `encompassingEncounter`
	var encounter = doc.getElementsByTagNameNS("urn:hl7-org:v3", "encompassingEncounter");

	if (encounter != null && encounter.getLength() > 0) {
	    var encounterEffectiveTime = getEncounterEffectiveTime(encounter);	
	    transformer = generateSHA256Id(encounter, "encounter", "encounterResourceId", transformer, doc, encounterEffectiveTime);
	} else {
	    // Try fallback to `encounter`
	    var encounters = doc.getElementsByTagNameNS("urn:hl7-org:v3", "encounter");

	    if (encounters != null && encounters.getLength() > 0) {
	        var encounterEffectiveTime = getEncounterEffectiveTime(encounters);
	        transformer = generateSHA256Id(encounters, "encounter", "encounterResourceId", transformer, doc, encounterEffectiveTime);
	    } else {
	        logger.error("Encounter resource not found in the XML.");
	    }
	}

	///Grouper Observation Resource 
	var mrn = channelMap.get('Patient-MRN') || "";
     var cin = channelMap.get('patientCIN') || "";
     var currentTimestamp = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
	var combinedText = mrn + cin + 'Grouper Observation Resource' + currentTimestamp;
     var sha256Hash = generateSHA256(new java.lang.String(combinedText)); 

     transformer.setParameter('grouperObservationResourceSha256Id', sha256Hash);
     logger.info('grouperObservationResourceSha256Id' + " : " + sha256Hash);

     transformer = getObservationCategoryCodes(transformer, doc); //observations

     //For Epic files, use the value of the header variable,'X-TechBD-Screening-Code', as the Grouper Screening Code, otherwise use the LOINC code '100698-0'
     //For Medent files, take the value from code.code
     var grouperScreeningCode = channelMap.get('X-TechBD-Screening-Code');
     if (channelMap.get('ehrVendor') == 'Epic' && (!grouperScreeningCode || grouperScreeningCode.trim() === '')) {
	    grouperScreeningCode = '100698-0';
	    logger.info("Defaulting grouperScreeningCode to 100698-0 for Epic");
	}
	if (!grouperScreeningCode) {
	    grouperScreeningCode = '';
	}
     transformer.setParameter('grouperScreeningCode', String(grouperScreeningCode));
     logger.info('grouperScreeningCode' + " : " + grouperScreeningCode);

	return transformer;
}

/**
* Util function to get FHIR resource profile URLs and set as parameters to the transformer which converts the XML file to FHIR Bundle.
*/
function set_fhir_resource_profile_urls(transformer) {
	var baseFhirUrl = java.lang.System.getenv("BASE_FHIR_URL");   
	var bundleMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_BUNDLE"); 
	var patientMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_PATIENT"); 
	var encounterMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_ENCOUNTER"); 
	var consentMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_CONSENT"); 
