	var observationSexualOrientationMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_SEXUAL_ORIENTATION"); 
	var questionnaireMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_QUESTIONNAIRE"); 
	var questionnaireResponseMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_QUESTIONNAIRE_RESPONSE"); 
	var practitionerMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_PRACTITIONER");
	var procedureMetaProfileUrl = java.lang.System.getenv("PROFILE_URL_PROCEDURE");

	if(baseFhirUrl != null) {
		transformer.setParameter("baseFhirUrl", baseFhirUrl);
