		logger.info("baseFhirUrl: " + baseFhirUrl);
	} else {
		var errorMessage = 'BASE_FHIR_URL variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
	}

	if(bundleMetaProfileUrl != null) {
		transformer.setParameter("bundleMetaProfileUrl", bundleMetaProfileUrl);
		channelMap.put('bundleMetaProfileUrl', bundleMetaProfileUrl);
		logger.info("bundleMetaProfileUrl: " + bundleMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_BUNDLE variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
	}

	if(patientMetaProfileUrl != null) {
		transformer.setParameter("patientMetaProfileUrl", patientMetaProfileUrl);
		channelMap.put('patientMetaProfileUrl', patientMetaProfileUrl);
		logger.info("patientMetaProfileUrl: " + patientMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_PATIENT variable is not set';
		logger.error(errorMessage);
