	} else {
		var errorMessage = 'PROFILE_URL_ENCOUNTER variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
	}

	if(consentMetaProfileUrl != null) {
		transformer.setParameter("consentMetaProfileUrl", consentMetaProfileUrl);
		channelMap.put('consentMetaProfileUrl', consentMetaProfileUrl);
		logger.info("consentMetaProfileUrl: " + consentMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_CONSENT variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
	}

	if(organizationMetaProfileUrl != null) {
		transformer.setParameter("organizationMetaProfileUrl", organizationMetaProfileUrl);
		channelMap.put('organizationMetaProfileUrl', organizationMetaProfileUrl);
		logger.info("organizationMetaProfileUrl: " + organizationMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_ORGANIZATION variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
	}

	if(observationMetaProfileUrl != null) {
		transformer.setParameter("observationMetaProfileUrl", observationMetaProfileUrl);
		channelMap.put('observationMetaProfileUrl', observationMetaProfileUrl);
		logger.info("observationMetaProfileUrl: " + observationMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_OBSERVATION variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
