		channelMap.put('observationSexualOrientationMetaProfileUrl', observationSexualOrientationMetaProfileUrl);
		logger.info("observationSexualOrientationMetaProfileUrl: " + observationSexualOrientationMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_SEXUAL_ORIENTATION variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
