	}

	if(questionnaireMetaProfileUrl != null) {
		transformer.setParameter("questionnaireMetaProfileUrl", questionnaireMetaProfileUrl);
		channelMap.put('questionnaireMetaProfileUrl', questionnaireMetaProfileUrl);
		logger.info("questionnaireMetaProfileUrl: " + questionnaireMetaProfileUrl);
	} else {
		var errorMessage = 'PROFILE_URL_QUESTIONNAIRE variable is not set';
		logger.error(errorMessage);
		setErrorResponse(500, errorMessage); // Set the HTTP response status to 500 (Server error)
		throw errorMessage; // Stop further processing by throwing an exception
