		// Remove any trailing non-XML content
		/*var lastClosingTagIndex = xmlDoc.lastIndexOf('>');
		if (lastClosingTagIndex !== xmlDoc.length - 1) {
		    xmlDoc = xmlDoc.substring(0, lastClosingTagIndex + 1);
		}*/
		var lastClosingClinicalDocumentTagIndex = xmlDoc.lastIndexOf('</ClinicalDocument>');
		if (lastClosingClinicalDocumentTagIndex !== xmlDoc.length - 1) {
		    xmlDoc = xmlDoc.substring(0, lastClosingClinicalDocumentTagIndex + 19);
		}

		// Extract the XML content from the form-data (i.e., skip the metadata)
	     var xmlStartIndex = xmlDoc.trim().indexOf('<?xml'); // Find the XML declaration
	     var sourceXml = xmlDoc.substring(xmlStartIndex); // Extract the XML part

	     if (!sourceXml || sourceXml.length === 0) {
	         logger.error("No XML data received in the request.");
	         responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome("No XML data received in the request.")));
	         throw new Error("No XML data received in the request.");
	     }

		logger.info("Source XML Content: " + sourceXml); 
		//**////////////////////////////////////////////////**///

		//Replace the space in between "X-SDOH-FLO-1570000066-Patient unable to answer" with hiphen
		sourceXml = sourceXml.replace(/X-SDOH-FLO-1570000066-Patient unable to answer/g, "X-SDOH-FLO-1570000066-Patient-unable-to-answer");
		sourceXml = sourceXml.replace(/X-SDOH-FLO-1570000066-Patient declined/g, "X-SDOH-FLO-1570000066-Patient-declined");

		validateLowHighDates(sourceXml); //Check the low and high dates in address

		/*Phi filter ccda file before validation*/
	     getManufacturerModelName(sourceXml); // Load the XSLT template
	     getConsentResourceStatus(sourceXml); // Check the existance of Consent section in the CCDA xml file and set the header variable 'X-TechBD-Elaboration'

		// Load the XSLT template
		var xsltPath = channelMap.get('cdaPhiFilter');
		var xsltFile = new java.io.File(xsltPath);
		var xsltStream = new java.io.FileInputStream(xsltFile);

		// Create the transformer
		var transformerFactory = javax.xml.transform.TransformerFactory.newInstance();
		var xsltSource = new javax.xml.transform.stream.StreamSource(xsltStream);
		var transformer = transformerFactory.newTransformer(xsltSource);

		// Prepare input and output streams
		var xmlInputStream = new java.io.StringReader(sourceXml);
		var xmlOutputStream = new java.io.StringWriter();
		var originalCcdFile = new javax.xml.transform.stream.StreamSource(xmlInputStream);
		var phiFilterOutput = new javax.xml.transform.stream.StreamResult(xmlOutputStream);

		// Perform the transformation
		try {
			transformer.transform(originalCcdFile, phiFilterOutput);

			// Store the transformed XML in the channel map
			var phiFilteredXml = xmlOutputStream.toString();
			channelMap.put('phi_filtered_ccd', phiFilteredXml);
			logger.info("PHI Filtered CCD: " + phiFilteredXml);
		} catch (e) {
	            var errorMsg;
	            if (e instanceof JavaException) {
	                errorMsg = "CCD XML phi filtering failed: " + e.toString();
	            } else {
	                errorMsg = "Unexpected error during phi filtering: " + e.message;
	            }

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));
	            logger.error(errorMsg);
	            throw new Error(errorMsg);
	     }
		/*End of phi filter*/

	//**/////////CCDA Validation Begin///////////////**///
		   // Validate CCD
	        var xsdFilePath = channelMap.get('xsdFilePath');
	        logger.info("Using XSD file path: " + xsdFilePath);    

	        // Load the XSD schema
	        var xsdFile = new java.io.File(xsdFilePath);
	        var schemaInputStream = new javax.xml.transform.stream.StreamSource(xsdFile);
	        var schema = javax.xml.validation.SchemaFactory
	            .newInstance(javax.xml.XMLConstants.W3C_XML_SCHEMA_NS_URI)
	            .newSchema(schemaInputStream);

	        var validator = schema.newValidator(); // Set up the validator       
	        //var xmlSource = new javax.xml.transform.stream.StreamSource(new java.io.StringReader(sourceXml)); // Set up the input source for the XML
	        var xmlSource = new javax.xml.transform.stream.StreamSource(new java.io.StringReader(phiFilteredXml)); // Set up the input source for the XML

	        // Perform the validation
	        try {
	        	validator.validate(xmlSource);
	          logger.info("CCD XML is valid according to the XSD.");            
	        } catch (e) {
