	            if (e instanceof JavaException) {
	                errorMsg = "CCD XML validation failed: " + e.toString();
	            } else {
	                errorMsg = "Unexpected error during validation: " + e.message;
	            }

	            // Failure: Return an OperationOutcome JSON response with validation errors
	            responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "invalid")));



