			var currentTimestamp = new java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SS'Z'").format(new java.util.Date());
			transformer.setParameter("currentTimestamp", currentTimestamp); // Pass the parameter to XSLT
			transformer = set_fhir_resource_profile_urls(transformer); // Pass all the profile urls as parameters to XSLT

			// Retrieve the X-TechBD-Base-FHIR-URL header
			var customBaseFhirUrl = $('headers').getHeader('X-TechBD-Base-FHIR-URL');
			logger.info("X-TechBD-Base-FHIR-URL: " + customBaseFhirUrl);

			// Retrieve the VALID_URLS environment variable (comma-separated list)
			var validUrlsEnv = Packages.java.lang.System.getenv("MC_VALID_FHIR_URLS");
			logger.info("MC_VALID_FHIR_URLS from environment: " + validUrlsEnv);

			// Convert VALID_URLS to an array for validation
			var validUrls = validUrlsEnv ? validUrlsEnv.split(",").map(function(url) { return url.trim(); }) : [];

			// Check if the header value is valid
			if (customBaseFhirUrl !== null && customBaseFhirUrl.trim() !== "") {
			    if (validUrls.includes(customBaseFhirUrl)) {
			        // Store valid value in channelMap
			        transformer.setParameter("baseFhirUrl", customBaseFhirUrl);
				   channelMap.put('baseFhirUrl', customBaseFhirUrl);
			    } else {
			        // Throw a Bad Request error if the URL is not in the valid list
					var errorMessage = 'Bad Request: The provided X-TechBD-Base-FHIR-URL is invalid.';
					logger.error(errorMessage);
					setErrorResponse(400, errorMessage); // Set the HTTP response status to 400 (Bad Request)
					throw errorMessage; // Stop further processing by throwing an exception
			    }
