				        			    logger.error("Error during CCD FHIR bundle transformation: " + e);			    throw e;			}


			// Convert the XML document to JSON
			const jsonResult = cleanedJsonString;
			channelMap.put('jsonResult', jsonResult);
			responseMap.put('finalResponse', JSON.stringify(jsonResult));

		    return 'dest_bundle'; // HTTP Sender

		} catch (e) {
			var errorMsg = "Error processing XML file upload: " + e.message;
	         // Failure: Return an OperationOutcome JSON response for general errors
	         responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));

		    logger.error("Error during transformation: " + e);
