		}
	} catch (e) {
	        var errorMsg = "Error processing XML file upload: " + e.message;

	        // Failure: Return an OperationOutcome JSON response for general errors
	        responseMap.put('finalResponse', JSON.stringify(getJsonInvalidOperationOutcome(errorMsg, "exception")));
